import React, { useState } from 'react';
import { useLanguage } from './context/LanguageContext';
import LanguageSelector from './components/LanguageSelector';
import './App.css';

function App() {
  const { t } = useLanguage();

  const [showPhone, setShowPhone] = useState(false);
  const [showCopied, setShowCopied] = useState(false);
  const [showImageModal, setShowImageModal] = useState(false);

  const email = 'davidcigaran@gmail.com';

  const renderHTML = (text) => {
    return { __html: text };
  };

  const currentYear = new Date().getFullYear();

  const handleCopyEmail = () => {
    navigator.clipboard.writeText(email);
    setShowCopied(true);

    setTimeout(() => {
      setShowCopied(false);
    }, 2000);
  };

  return (
    <div className="App">
      {/* Header */}
      <header className="header">
        <div className="language-selector-top-left">
          <LanguageSelector />
        </div>

        <nav className="nav">
          <a href="/" className="logo">{t('nav.logo')}</a>

          <div className="nav-links">    
            <a href="#certifications">{t('nav.certifications')}</a>
            <a href="#experience">{t('nav.experience')}</a>
            <a href="#about">{t('nav.about')}</a>
            <a href="#a_history">{t('nav.education')}</a>

            <div 
              className="contact-item"
              onMouseEnter={() => setShowPhone(true)}
              onMouseLeave={() => setShowPhone(false)}
            >
              <a href="#contact" className="contact-link">
                {t('nav.contact')}
              </a>
              {showPhone && (
                <div className="phone-tooltip">
                  <span className="phone-number">688 84 65 27</span>
                  <div className="phone-tooltip-arrow"></div>
                </div>
              )}
            </div>
          </div>

          <div className="nav-right">
            <div className="header-buttons">
              <a 
                href="https://www.youtube.com/@TuCanalDeYouTube" 
                target="_blank" 
                rel="noopener noreferrer"
                className="project-link"
              >
                {t('nav.projects')}
              </a>

              <div className="social">
                <a href="https://linkedin.com/in/davidorregod" target="_blank" rel="noopener noreferrer">
                  {t('hero.linkedin')}
                </a>
                <a href="https://github.com/davidorregod" target="_blank" rel="noopener noreferrer">
                  {t('hero.github')}
                </a>
              </div>
            </div>

            <div className="language-selector">
              <LanguageSelector />
            </div>
          </div>
        </nav>
      </header>

      {/* Hero */}
      <section className="hero">
        <div className="hero-content">
          <div
            className="hero-image"
            onClick={() => setShowImageModal(true)}
            style={{ cursor: 'pointer' }}
          >
            <img 
              src="https://davidorrego.dev/images/profile-new.svg" 
              alt="Profile"
              className="profile-img"
            />
          </div>

          <div className="hero-text">
            <h1 dangerouslySetInnerHTML={renderHTML(t('hero.title'))} />
            <p className="tagline">{t('hero.tagline')}</p>
            <p 
              className="description" 
              dangerouslySetInnerHTML={renderHTML(t('hero.description'))}
            />

            <div className="hero-buttons">
              <a href="https://linkedin.com/in/davidorregod" className="btn">
                {t('hero.linkedin')}
              </a>

              <a href="https://youtube.com" className="btn">
                {t('hero.youtube')}
              </a>

              <button onClick={handleCopyEmail} className="btn email-btn">
                {t('hero.email')}
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* Modal imagen */}
      {showImageModal && (
        <div className="image-modal" onClick={() => setShowImageModal(false)}>
          <div className="image-modal-content" onClick={(e) => e.stopPropagation()}>
            <button
              className="image-modal-close"
              onClick={() => setShowImageModal(false)}
            >
              âœ•
            </button>
            <img
              src="https://davidorrego.dev/images/profile-new.svg"
              alt="Profile large"
            />
          </div>
        </div>
      )}

      {/* Toast copiado */}
      {showCopied && (
        <div className="copy-toast">
          ðŸ“‹ Email copiado al portapapeles
        </div>
      )}

      {/* Footer */}
      <footer className="footer">
        <p>{t('footer.inspired')}</p>
        <p>
          {t('footer.copyright')
            .replace('[AÃ‘O]', currentYear)
            .replace('[TU NOMBRE]', 'Tu Nombre')}
        </p>
      </footer>

      <div className="background-effect"></div>
    </div>
  );
}

export default App;
