import React, { useState, useEffect } from 'react';
import { useLanguage } from './context/LanguageContext';
import LanguageSelector from './components/LanguageSelector';
import './App.css';

function App() {
  const { t } = useLanguage();
  const [showPhone, setShowPhone] = useState(false);
  const [showCopied, setShowCopied] = useState(false);
  const [showImageModal, setShowImageModal] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const email = 'davidcigaran@gmail.com';

  const certifications = [
    {
      id: 1,
      title: 'eJPT',
      image: 'img/ejpt.png',
      expedition: '2026',
      link: 'cert/ejpt'
    },
    {
      id: 2,
      title: 'Cibersecurity Profile Recon',
      image: 'img/f3.png',
      expedition: '2025',
      link: 'cert/cypr'
    },
    {
      id: 3,
      title: 'Burp Suite Certified',
      image: 'img/portswigger.png',
      expedition: '2026',
      link: 'cert/portswigger'
    },
    {
      id: 4,
      title: 'AWS',
      image: 'img/aws.png',
      expedition: '2022',
      link: 'cert/aws'
    }
  ];

  const renderHTML = (text) => {
    return { __html: text };
  };

  const currentYear = new Date().getFullYear();

  const handleCopyEmail = () => {
    navigator.clipboard.writeText(email);
    setShowCopied(true);
    setTimeout(() => {
      setShowCopied(false);
    }, 2000);
  };

  const handleVerMas = (link) => {
    window.open(link, '_blank');
  };

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex === certifications.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex === 0 ? certifications.length - 1 : prevIndex - 1
    );
  };

  // Auto-rotate carousel
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 5000);
    return () => clearInterval(interval);
  }, [currentIndex]);

  return (
    <div className="App">
      {/* Header */}
      <header className="header">
        <div className="language-selector-top-left">
          <LanguageSelector />
        </div>
        <nav className="nav">
          <a href="/" className="logo">{t('nav.logo')}</a>
          <div className="nav-links">
            <a href="#certifications">{t('nav.certifications')}</a>
            <a href="#experience">{t('nav.experience')}</a>
            <a href="#about">{t('nav.about')}</a>
            <a href="#a_history">{t('nav.education')}</a>
            <div 
              className="contact-item" 
              onMouseEnter={() => setShowPhone(true)}
              onMouseLeave={() => setShowPhone(false)}
            >
              <a href="#contact" className="contact-link">
                {t('nav.contact')}
              </a>
              {showPhone && (
                <div className="phone-tooltip">
                  <span className="phone-number">688 84 65 27</span>
                  <div className="phone-tooltip-arrow"></div>
                </div>
              )}
            </div>
          </div>
          <div className="nav-right">
            <div className="header-buttons">
              <a 
                href="https://www.youtube.com/@TuCanalDeYouTube" 
                target="_blank" 
                rel="noopener noreferrer" 
                className="project-link"
              >
                {t('nav.projects')}
              </a>
              <div className="social">
                <a 
                  href="https://linkedin.com/in/davidorregod" 
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  {t('hero.linkedin')}
