import React, { useState, useEffect, useRef } from 'react';
import { useLanguage } from './context/LanguageContext';
import LanguageSelector from './components/LanguageSelector';
import './App.css';

function App() {
  const { t } = useLanguage();
  const [showPhone, setShowPhone] = useState(false);
  const [showCopied, setShowCopied] = useState(false);
  const [showImageModal, setShowImageModal] = useState(false);
  const [currentCertIndex, setCurrentCertIndex] = useState(0);
  const [currentToolIndex, setCurrentToolIndex] = useState(0);
  const [currentExpIndex, setCurrentExpIndex] = useState(0); // Nuevo estado para el slider de experiencia
  const carouselRef = useRef(null);
  const toolsCarouselRef = useRef(null);
  const expCarouselRef = useRef(null); // Nueva referencia para el slider de experiencia
  const email = 'davidcigaran@gmail.com';

  const certifications = [
    { id: 1, title: 'eJPT', image: '/img/ejpt.png', expedition: '2026' },
    { id: 2, title: 'Cibersecurity Profile Recon', image: '/img/f3.png', expedition: '2025' },
    { id: 3, title: 'Burp Suite Certified', image: '/img/portswigger.png', expedition: '2026' },
    { id: 4, title: 'AWS', image: '/img/aws.png', expedition: '2022' }
  ];

  const educationHistory = [
    { id: 1, title: 'EducaciÃ³n Secundaria Obligatoria (E.S.O)', date: '2019', institution: 'Ntra.Sra del Rosario Dominicas Barakaldo' },
    { id: 2, title: 'Grado Medio HostelerÃ­a y Turismo', date: '2019-2021', institution: 'UPV/EHU Leioa' },
    { id: 3, title: 'Aprendizaje de metodologÃ­as similares a S.M.R (Sistemas MicroinformÃ¡ticos en Redes)', date: '2021-2022', institution: 'Campus Training Bilbao' },
    { id: 4, title: 'AdministraciÃ³n de Sistemas InformÃ¡ticos en Redes (A.S.I.R)', date: '2023-2025', institution: 'Centro de Estudios ALMI (Deusto)' },
    { id: 5, title: 'Bootcamp de Ciberseguridad', date: '2025-2026', institution: 'The Bridge' }
  ];

  // Nueva estructura de datos para el slider de experiencia
  const experienceSliderData = [
    {
      id: 1,
      type: 'professional',
      title: 'Centro de Estudios ALMI',
      subtitle: 'PrÃ¡cticas Profesionales',
      period: 'Febrero 2025 â€“ Mayo 2025',
      icon: 'ðŸ’¼',
      intro: 'Durante mis prÃ¡cticas profesionales en el Centro de Estudios ALMI, participÃ© en el desarrollo y adaptaciÃ³n de la pÃ¡gina web del Centro de DÃ­a de Basurto, ajustÃ¡ndola a los requisitos funcionales y tÃ©cnicos solicitados.',
      responsibilities: [
        { icon: 'ðŸ“¤', text: 'ImportaciÃ³n y exportaciÃ³n completa del sitio web, gestionando contenido y estructura.' },
        { icon: 'ðŸ—„', text: 'CreaciÃ³n, configuraciÃ³n y mantenimiento de bases de datos.' },
        { icon: 'ðŸ”Œ', text: 'GestiÃ³n y optimizaciÃ³n de plugins para el correcto uso del personal y usuarios.' },
        { icon: 'ðŸ“', text: 'ElaboraciÃ³n de documentaciÃ³n tÃ©cnica y registro de cambios realizados.' }
      ],
      technologies: ['WordPress', 'MySQL', 'PHP', 'HTML', 'CSS', 'JavaScript']
    },
    {
      id: 2,
      type: 'academic',
      title: 'Proyectos AcadÃ©micos',
      subtitle: 'The Bridge & FormaciÃ³n ASIR',
      period: '2023 â€“ 2026',
      icon: 'ðŸŽ“',
      intro: 'Desarrollo de proyectos prÃ¡cticos enfocados en ciberseguridad y administraciÃ³n de sistemas.',
      projects: [
