import React, { useState, useEffect, useRef } from 'react';
import { useLanguage } from './context/LanguageContext';
import LanguageSelector from './components/LanguageSelector';
import './App.css';

function App() {
  const { t } = useLanguage();
  const [showPhone, setShowPhone] = useState(false);
  const [showCopied, setShowCopied] = useState(false);
  const [showImageModal, setShowImageModal] = useState(false);
  const [currentCertIndex, setCurrentCertIndex] = useState(0);
  const [currentToolCategory, setCurrentToolCategory] = useState(0);
  const carouselRef = useRef(null);
  const toolsCarouselRef = useRef([]);
  const email = 'davidcigaran@gmail.com';

  // Herramientas organizadas por categorías con paginación
  const herramientasCategorias = [
    {
      id: 0,
      name: 'Sistemas Operativos',
      description: 'Distribuciones y sistemas utilizados',
      tools: [
        { id: 1, name: 'Kali', icon: '/img/kali.png' },
        { id: 2, name: 'Parrot', icon: '/img/parrot.png' },
        { id: 3, name: 'Ubuntu && Ubuntu Server', icon: '/img/ubu.png' },
        { id: 4, name: 'Windows 7', icon: '/img/w7.jpeg' },
        { id: 5, name: 'Windows 10', icon: '/img/w10.png' },
        { id: 6, name: 'Windows Server', icon: '/img/w_server.png' },
        { id: 7, name: 'Zorin', icon: '/img/zorin.png' },
        { id: 8, name: 'Tails', icon: '/img/tails.png' }
      ],
      effect: 'circular' // Efecto específico para esta categoría
    },
    {
      id: 1,
      name: 'OSINT',
      description: 'Inteligencia de Fuentes Abiertas',
      tools: [
        { id: 9, name: 'Google Dorks', icon: '/img/Dorks.webp' },
        { id: 10, name: 'Censys', icon: '/img/cen.png' },
        { id: 11, name: 'Shodan', icon: '/img/shodan.jpeg' },
        { id: 12, name: 'SpiderFoot', icon: '/img/spiderfoot.png' },
        { id: 13, name: 'FOCA', icon: '/img/FOCA.png' },
        { id: 14, name: 'Maltego', icon: '/img/maltego.png' },
        { id: 15, name: 'HTTrack', icon: '/img/httrack.png' },
        { id: 16, name: 'ExifTool', icon: '/img/exiftool.png' }
      ],
      effect: 'radar' // Efecto específico para esta categoría
    },
    {
      id: 2,
      name: 'Escaneo Web y Fuzzing',
      tools: [
        { id: 17, name: 'Nikto', icon: '/img/nikto.jpeg' },
        { id: 18, name: 'Dirb', icon: '/img/dirb.png' },
        { id: 19, name: 'DirBuster', icon: '/img/dirbuster.png' },
        { id: 20, name: 'DirSearch', icon: '/img/dirsearch.png' },
        { id: 21, name: 'GoBuster', icon: '/img/gobuster.png' },
        { id: 22, name: 'Burp Suite', icon: '/img/Burp_suite.png' },
        { id: 23, name: 'SQLMap', icon: '/img/sqlmap.png' }
      ],
      effect: 'wave' // Efecto específico para esta categoría
    },
    {
      id: 3,
      name: 'Análisis de Red',
      tools: [
        { id: 24, name: 'Nmap', icon: '/img/nmap.jpeg' },
        { id: 25, name: 'ARP-Scan', icon: '/img/arp.png' },
        { id: 26, name: 'Ping', icon: '/img/ping.jpeg' },
        { id: 27, name: 'cURL', icon: '/img/curl.png' },
        { id: 28, name: 'Netcat', icon: '/img/netcat.png' },
        { id: 29, name: 'Wireshark', icon: '/img/wireshark.jpeg' },
        { id: 30, name: 'OpenSSL', icon: '/img/OpenSSL.jpg' }
      ],
      effect: 'network' // Efecto específico para esta categoría
    },
    {
      id: 4,
      name: 'Escaneo y Virtualización',
      tools: [
        { id: 31, name: 'Nessus', icon: '/img/nessus.png' },
        { id: 32, name: 'Greenbone', icon: '/img/greenbone.png' },
        { id: 33, name: 'DVWA', icon: '/img/dvwa.jpeg' },
        { id: 34, name: 'AWS', icon: '/img/aws.png' },
        { id: 35, name: 'VirtualBox', icon: '/img/vbox.png' }
      ],
      effect: 'glitch' // Efecto específico para esta categoría
    },
    {
      id: 5,
      name: 'Control y Explotación',
      tools: [
        { id: 36, name: 'Hydra', icon: '/img/hidra.png' },
        { id: 37, name: 'John', icon: '/img/john.png' },
        { id: 38, name: 'Hash-Identifier', icon: '/img/hash.jpeg' },
        { id: 39, name: 'Decode', icon: '/img/decode.png' },
        { id: 40, name: 'Metasploit', icon: '/img/meta.png' },
        { id: 41, name: 'Bash', icon: '/img/bash.png' },
        { id: 42, name: 'MySQL', icon: '/img/mysql.png' }
      ],
      effect: 'hack' // Efecto específico para esta categoría
    }
  ];

  const certifications = [
    { id: 1, title: 'eJPT', image: '/img/ejpt.png', expedition: '2026' },
    { id: 2, title: 'Cibersecurity Profile Recon', image: '/img/f3.png', expedition: '2025' },
    { id: 3, title: 'Burp Suite Certified', image: '/img/portswigger.png', expedition: '2026' },
    { id: 4, title: 'AWS', image: '/img/aws.png', expedition: '2022' }
  ];

  const educationHistory = [
    { id: 1, title: 'Educación Secundaria Obligatoria (E.S.O)', date: '2019', institution: 'Ntra.Sra del Rosario Dominicas Barakaldo' },
    { id: 2, title: 'Grado Medio Hostelería y Turismo', date: '2019-2021', institution: 'UPV/EHU Leioa' },
    { id: 3, title: 'Aprendizaje de metodologías similares a S.M.R (Sistemas Microinformáticos en Redes)', date: '2021-2022', institution: 'Campus Training Bilbao' },
    { id: 4, title: 'Administración de Sistemas Informáticos en Redes (A.S.I.R)', date: '2023-2025', institution: 'Centro de Estudios ALMI (Deusto)' },
    { id: 5, title: 'Bootcamp de Ciberseguridad', date: '2025-2026', institution: 'The Bridge' }
  ];

  // Estado para el slider de cada categoría (mostrar de 6 en 6)
  const [toolSlides, setToolSlides] = useState(
    herramientasCategorias.reduce((acc, categoria) => {
      acc[categoria.id] = 0;
      return acc;
    }, {})
  );

  const renderHTML = (text) => {
    return { __html: text };
  };

  const currentYear = new Date().getFullYear();

  const handleCopyEmail = () => {
    navigator.clipboard.writeText(email);
    setShowCopied(true);
    setTimeout(() => {
      setShowCopied(false);
    }, 2000);
  };

  const nextCertSlide = () => {
    setCurrentCertIndex((prevIndex) => 
      prevIndex === certifications.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevCertSlide = () => {
    setCurrentCertIndex((prevIndex) => 
      prevIndex === 0 ? certifications.length - 1 : prevIndex - 1
    );
  };

  const nextToolSlide = (categoryId) => {
    const categoria = herramientasCategorias.find(c => c.id === categoryId);
    const totalSlides = Math.ceil(categoria.tools.length / 6);
    setToolSlides(prev => ({
      ...prev,
      [categoryId]: (prev[categoryId] + 1) % totalSlides
    }));
  };

  const prevToolSlide = (categoryId) => {
    const categoria = herramientasCategorias.find(c => c.id === categoryId);
    const totalSlides = Math.ceil(categoria.tools.length / 6);
    setToolSlides(prev => ({
      ...prev,
      [categoryId]: (prev[categoryId] - 1 + totalSlides) % totalSlides
    }));
  };

  const nextToolCategory = () => {
    setCurrentToolCategory((prevIndex) => 
      prevIndex === herramientasCategorias.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevToolCategory = () => {
    setCurrentToolCategory((prevIndex) => 
      prevIndex === 0 ? herramientasCategorias.length - 1 : prevIndex - 1
    );
  };

  // Obtener herramientas visibles por slide (6 por slide)
  const getVisibleTools = (categoria) => {
    const startIndex = toolSlides[categoria.id] * 6;
    const endIndex = startIndex + 6;
    return categoria.tools.slice(startIndex, endIndex);
  };

  // Efecto de partículas por categoría
  const renderEffect = (effectType) => {
    switch(effectType) {
      case 'circular':
        return (
          <div className="effect-circular">
            {[...Array(12)].map((_, i) => (
              <div key={i} className="circular-particle"></div>
            ))}
          </div>
        );
      case 'radar':
        return (
          <div className="effect-radar">
            <div className="radar-sweep"></div>
            {[...Array(8)].map((_, i) => (
              <div key={i} className="radar-dot"></div>
            ))}
          </div>
        );
      case 'wave':
        return (
          <div className="effect-wave">
            {[...Array(5)].map((_, i) => (
              <div key={i} className="wave-particle"></div>
            ))}
          </div>
        );
      case 'network':
        return (
          <div className="effect-network">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="network-node">
                <div className="network-connection"></div>
              </div>
            ))}
          </div>
        );
      case 'glitch':
        return (
          <div className="effect-glitch">
            <div className="glitch-line"></div>
            <div className="glitch-line"></div>
            <div className="glitch-line"></div>
          </div>
        );
      case 'hack':
        return (
          <div className="effect-hack">
            {[...Array(10)].map((_, i) => (
              <div key={i} className="hack-particle"></div>
            ))}
            <div className="hack-terminal">_</div>
          </div>
        );
      default:
        return null;
    }
  };

  // Auto-rotate carousels
  useEffect(() => {
    const certInterval = setInterval(() => {
      nextCertSlide();
    }, 5000);

    const toolCategoryInterval = setInterval(() => {
      nextToolCategory();
    }, 10000);

    return () => {
      clearInterval(certInterval);
      clearInterval(toolCategoryInterval);
    };
  }, [currentCertIndex, currentToolCategory]);

  return (
    <div className="App">
      <header className="header">
        <div className="language-selector-top-left">
          <LanguageSelector />
        </div>
        <nav className="nav">
          <a href="/" className="logo">{t('nav.logo')}</a>
          <div className="nav-links">
            <a href="#certifications">{t('nav.certifications')}</a>
            <a href="#experience">{t('nav.experience')}</a>
            <a href="#about">{t('nav.about')}</a>
            <a href="#herramientas">Herramientas</a>
            <a href="#a_history">{t('nav.education')}</a>
            <div 
              className="contact-item" 
              onMouseEnter={() => setShowPhone(true)}
              onMouseLeave={() => setShowPhone(false)}
            >
              <a href="#contact" className="contact-link">
                {t('nav.contact')}
              </a>
              {showPhone && (
                <div className="phone-tooltip">
                  <span className="phone-number">688 84 65 27</span>
                  <div className="phone-tooltip-arrow"></div>
                </div>
              )}
            </div>
          </div>
          <div className="nav-right">
            <div className="header-buttons">
              <a 
                href="https://www.youtube.com/@TuCanalDeYouTube" 
                target="_blank" 
                rel="noopener noreferrer" 
                className="project-link"
              >
                {t('nav.projects')}
              </a>
              <div className="social">
                <a 
                  href="https://linkedin.com/in/davidorregod" 
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  {t('hero.linkedin')}
                </a>
                <a 
                  href="https://github.com/davidorregod" 
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  {t('hero.github')}
                </a>
              </div>
            </div>
            <div className="language-selector">
              <LanguageSelector />
            </div>
          </div>
        </nav>
      </header>

      <section className="hero">
        <div className="hero-content">
          <div 
            className="hero-image" 
            onClick={() => setShowImageModal(true)}
            title="Click para ampliar"
          >
            <img 
              src="https://davidorrego.dev/images/profile-new.svg" 
              alt="Profile" 
              className="profile-img" 
            />
          </div>
          <div className="hero-text">
            <h1 dangerouslySetInnerHTML={renderHTML(t('hero.title'))} />
            <p className="tagline">{t('hero.tagline')}</p>
            <p className="description" dangerouslySetInnerHTML={renderHTML(t('hero.description'))} />
            <div className="hero-buttons">
              <a 
                href="https://linkedin.com/in/davidorregod" 
                className="btn"
              >
                {t('hero.linkedin')}
              </a>
              <a 
                href="https://youtube.com" 
                className="btn"
              >
                {t('hero.youtube')}
              </a>
              <button onClick={handleCopyEmail} className="btn email-btn">
                {t('hero.email')}
              </button>
            </div>
          </div>
        </div>
      </section>

      <section id="about" className="about-section">
        <div className="about-container">
          <div className="about-header">
            <h2>¿Quién Soy?</h2>
            <p className="about-subtitle">Aprendiz junior</p>
          </div>

          <div className="about-content">
            <div className="about-text">
              <p className="about-description">
                Mi nombre es David Cigarán Flores, tengo 23 años y recientemente he finalizado mis 
                estudios de Administración de Sistemas Informáticos en Red (ASIR) y el bootcamp de 
                Ciberseguridad en The Bridge, donde descubrí mi gusto por la seguridad ofensiva y 
                el análisis de vulnerabilidades, más específicamente en el ámbito del Red Team.
              </p>

              <p className="about-description">
                Durante mi formación he desarrollado una base sólida en redes, sistemas y metodologías 
                de pentesting, y sigo ampliando mis conocimientos por interés propio. Me considero una 
                persona responsable, con muchas ganas de aprender y con la capacidad de adaptarme 
                rápidamente a nuevos entornos. Disfruto trabajando en equipo, compartiendo ideas y 
                enfrentando retos técnicos que me permitan crecer.
              </p>

              <p className="about-description">
                Busco una oportunidad para iniciar mi carrera en un entorno donde pueda aprender de 
                expertos, enfrentar desafíos reales y aportar desde el primer día. Mi objetivo es seguir 
                mejorando poco a poco, reforzar mis habilidades y llegar a ser un buen profesional en 
                el mundo de la ciberseguridad ofensiva.
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* SECCIÓN HERRAMIENTAS - CARRUSEL POR CATEGORÍAS */}
      <section id="herramientas" className="section herramientas-section">
        <div className="container">
          <h2 className="section-title">Herramientas y Tecnologías</h2>
          <p className="section-subtitle">Organizadas por categorías (6 herramientas por vista)</p>

          {/* Carrusel de categorías */}
          <div className="categorias-carousel">
            <div className="categorias-wrapper">
              <div className="categorias-track" style={{ transform: `translateX(-${currentToolCategory * 100}%)` }}>
                {herramientasCategorias.map((categoria) => (
                  <div key={categoria.id} className="categoria-slide">
                    <div className="categoria-card">
                      {/* Efecto específico por categoría */}
                      {renderEffect(categoria.effect)}

                      <div className="categoria-header">
                        <h3 className="categoria-title">{categoria.name}</h3>
                        {categoria.description && (
                          <p className="categoria-description">{categoria.description}</p>
                        )}
                      </div>

                      {/* Carrusel de herramientas dentro de la categoría */}
                      <div className="herramientas-inner-carousel">
                        <div className="skill-items">
                          {getVisibleTools(categoria).map((tool) => (
                            <div key={tool.id} className="skill-item">
                              <div className="skill-icon">
                                <img 
                                  src={tool.icon} 
                                  alt={tool.name}
                                  onError={(e) => {
                                    e.target.style.display = 'none';
                                    e.target.parentElement.innerHTML = `
                                      <div class="icon-fallback">
                                        ${tool.name.charAt(0)}
                                      </div>
                                    `;
                                  }}
                                />
                              </div>
                              <span className="skill-name">{tool.name}</span>
                            </div>
                          ))}
                        </div>

                        {/* Controles del carrusel interno (solo si hay más de 6 herramientas) */}
                        {categoria.tools.length > 6 && (
                          <>
                            <button 
                              className="inner-carousel-btn prev-btn"
                              onClick={() => prevToolSlide(categoria.id)}
                            >
                              ‹
                            </button>
                            <button 
                              className="inner-carousel-btn next-btn"
                              onClick={() => nextToolSlide(categoria.id)}
                            >
                              ›
                            </button>

                            {/* Indicadores del carrusel interno */}
                            <div className="inner-carousel-indicators">
                              {Array.from({ length: Math.ceil(categoria.tools.length / 6) }).map((_, index) => (
                                <button
                                  key={index}
                                  className={`inner-indicator ${index === toolSlides[categoria.id] ? 'active' : ''}`}
                                  onClick={() => setToolSlides(prev => ({ ...prev, [categoria.id]: index }))}
                                />
                              ))}
                            </div>
                          </>
                        )}
                      </div>

                      <div className="categoria-footer">
                        <span className="tool-count">
                          {categoria.tools.length} herramientas en total
                        </span>
                        <span className="slide-info">
                          Vista {toolSlides[categoria.id] + 1} de {Math.ceil(categoria.tools.length / 6)}
                        </span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Controles del carrusel de categorías */}
            <button className="categoria-carousel-btn prev-btn" onClick={prevToolCategory}>
              ‹
            </button>
            <button className="categoria-carousel-btn next-btn" onClick={nextToolCategory}>
              ›
            </button>

            {/* Indicadores de categorías */}
            <div className="categoria-indicators">
              {herramientasCategorias.map((_, index) => (
                <button
                  key={index}
                  className={`categoria-indicator ${index === currentToolCategory ? 'active' : ''}`}
                  onClick={() => setCurrentToolCategory(index)}
                />
              ))}
            </div>
          </div>
        </div>
      </section>

      <section id="certifications" className="section certifications-section">
        <div className="container">
          <h2 className="section-title">Certificaciones e Insignias</h2>

          <div className="certifications-carousel" ref={carouselRef}>
            <div className="carousel-wrapper">
              <div className="carousel-track" style={{ transform: `translateX(-${currentCertIndex * 100}%)` }}>
                {[...certifications, ...certifications, ...certifications].map((cert, index) => (
                  <div key={`${cert.id}-${index}`} className="carousel-slide">
                    <div className="cert-card">
                      <img 
                        src={cert.image} 
                        alt={cert.title} 
                        className="cert-image"
                      />
                      <div className="cert-content">
                        <h3 className="cert-title">{cert.title}</h3>
                        <p className="cert-expedition">Expedición: {cert.expedition}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <button className="carousel-btn prev-btn" onClick={prevCertSlide}>
              ‹
            </button>
            <button className="carousel-btn next-btn" onClick={nextCertSlide}>
              ›
            </button>

            <div className="carousel-indicators">
              {certifications.map((_, index) => (
                <button
                  key={index}
                  className={`indicator ${index === currentCertIndex % certifications.length ? 'active' : ''}`}
                  onClick={() => setCurrentCertIndex(index)}
                />
              ))}
            </div>
          </div>
        </div>
      </section>

      <section id="a_history" className="section education-section">
        <div className="container">
          <h2 className="section-title education-title">Historial Académico</h2>

          <div className="timeline">
            {educationHistory.map((item, index) => (
              <div key={item.id} className="timeline-item">
                <div className="timeline-content">
                  <h3 className="timeline-title">{item.title}</h3>
                  <div className="timeline-date">{item.date}</div>
                  <p className="timeline-institution">{item.institution}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {showImageModal && (
        <div 
          className="modal-overlay"
          onClick={() => setShowImageModal(false)}
        >
          <img 
            src="https://davidorrego.dev/images/profile-new.svg" 
            alt="Profile ampliada" 
            className="modal-image"
            onClick={(e) => e.stopPropagation()}
          />
          <button 
            className="modal-close-btn"
            onClick={() => setShowImageModal(false)}
            aria-label="Cerrar imagen"
          >
            ✕
          </button>
        </div>
      )}

      {showCopied && (
        <div className="copy-toast">
          📋 Email copiado al portapapeles
        </div>
      )}

      <footer className="footer">
        <p>{t('footer.inspired')}</p>
        <p>
          {t('footer.copyright')
            .replace('[AÑO]', currentYear)
            .replace('[TU NOMBRE]', 'David Cigarán')}
        </p>
      </footer>
      <div className="background-effect"></div>
    </div>
  );
}

export default App;
