import React, { useState, useEffect, useRef } from 'react';
import { useLanguage } from './context/LanguageContext';
import LanguageSelector from './components/LanguageSelector';
import './App.css';

function App() {
  const { t } = useLanguage();
  const [showPhone, setShowPhone] = useState(false);
  const [showCopied, setShowCopied] = useState(false);
  const [showImageModal, setShowImageModal] = useState(false);
  const [currentCertIndex, setCurrentCertIndex] = useState(0);
  const [hoveredCard, setHoveredCard] = useState(null);
  const carouselRef = useRef(null);
  const email = 'davidcigaran@gmail.com';

  const todasLasHerramientas = [
    { id: 1, name: 'Kali', icon: 'img/kali.png' },
    { id: 2, name: 'Parrot', icon: 'img/parrot.png' },
    { id: 3, name: 'Ubuntu && Ubuntu Server', icon: 'img/ubu.png' },
    { id: 4, name: 'Windows 7', icon: 'img/w7.jpeg' },
    { id: 5, name: 'Windows 10', icon: 'img/w10.png' },
    { id: 6, name: 'Windows Server', icon: 'img/w_server.png' },
    { id: 7, name: 'Zorin', icon: 'img/zorin.png' },
    { id: 8, name: 'Tails', icon: 'img/tails.png' },
    { id: 9, name: 'Google Dorks', icon: 'img/Dorks.webp' },
    { id: 10, name: 'Censys', icon: 'img/cen.png' },
    { id: 11, name: 'Shodan', icon: 'img/shodan.jpeg' },
    { id: 12, name: 'SpiderFoot', icon: 'img/spiderfoot.png' },
    { id: 13, name: 'FOCA', icon: 'img/FOCA.png' },
    { id: 14, name: 'Maltego', icon: 'img/maltego.png' },
    { id: 15, name: 'HTTrack', icon: 'img/httrack.png' },
    { id: 16, name: 'ExifTool', icon: 'img/exiftool.png' },
    { id: 17, name: 'Nikto', icon: 'img/nikto.jpeg' },
    { id: 18, name: 'Dirb', icon: 'img/dirb.png' },
    { id: 19, name: 'DirBuster', icon: 'img/dirbuster.png' },
    { id: 20, name: 'DirSearch', icon: 'img/dirsearch.png' },
    { id: 21, name: 'GoBuster', icon: 'img/gobuster.png' },
    { id: 22, name: 'Burp Suite', icon: 'img/Burp_suite.png' },
    { id: 23, name: 'SQLMap', icon: 'img/sqlmap.png' },
    { id: 24, name: 'Nmap', icon: 'img/nmap.jpeg' },
    { id: 25, name: 'ARP-Scan', icon: 'img/arp.png' },
    { id: 26, name: 'Ping', icon: 'img/ping.jpeg' },
    { id: 27, name: 'cURL', icon: 'img/curl.png' },
    { id: 28, name: 'Netcat', icon: 'img/netcat.png' },
    { id: 29, name: 'Wireshark', icon: 'img/wireshark.jpeg' },
    { id: 30, name: 'OpenSSL', icon: 'img/OpenSSL.jpg' },
    { id: 31, name: 'Nessus', icon: 'img/nessus.png' },
    { id: 32, name: 'Greenbone', icon: 'img/greenbone.png' },
    { id: 33, name: 'DVWA', icon: 'img/dvwa.jpeg' },
    { id: 34, name: 'AWS', icon: 'img/aws.png' },
    { id: 35, name: 'VirtualBox', icon: 'img/vbox.png' },
    { id: 36, name: 'Hydra', icon: 'img/hidra.png' },
    { id: 37, name: 'John', icon: 'img/john.png' },
    { id: 38, name: 'Hash-Identifier', icon: 'img/hash.jpeg' },
    { id: 39, name: 'Decode', icon: 'img/decode.png' },
    { id: 40, name: 'Metasploit', icon: 'img/meta.png' },
    { id: 41, name: 'Bash', icon: 'img/bash.png' },
    { id: 42, name: 'MySQL', icon: 'img/mysql.png' }
  ];

  const certifications = [
    { id: 1, title: 'eJPT', image: 'img/ejpt.png', expedition: '2026' },
    { id: 2, title: 'Cibersecurity Profile Recon', image: 'img/f3.png', expedition: '2025' },
    { id: 3, title: 'Burp Suite Certified', image: 'img/portswigger.png', expedition: '2026' },
    { id: 4, title: 'AWS', image: 'img/aws.png', expedition: '2022' }
  ];

  const educationHistory = [
    { id: 1, title: 'Educación Secundaria Obligatoria (E.S.O)', date: '2019', institution: 'Ntra.Sra del Rosario Dominicas Barakaldo' },
    { id: 2, title: 'Grado Medio Hostelería y Turismo', date: '2019-2021', institution: 'UPV/EHU Leioa' },
    { id: 3, title: 'Aprendizaje de metodologías similares a S.M.R (Sistemas Microinformáticos en Redes)', date: '2021-2022', institution: 'Campus Training Bilbao' },
    { id: 4, title: 'Administración de Sistemas Informáticos en Redes (A.S.I.R)', date: '2023-2025', institution: 'Centro de Estudios ALMI (Deusto)' },
    { id: 5, title: 'Bootcamp de Ciberseguridad', date: '2025-2026', institution: 'The Bridge' }
  ];

  const currentYear = new Date().getFullYear();

  const handleCopyEmail = () => {
    navigator.clipboard.writeText(email);
    setShowCopied(true);
    setTimeout(() => setShowCopied(false), 2000);
  };

  const handleMouseEnter = (id) => setHoveredCard(id);
  const handleMouseLeave = () => setHoveredCard(null);

  const nextCertSlide = () => setCurrentCertIndex(prev => prev === certifications.length - 1 ? 0 : prev + 1);
  const prevCertSlide = () => setCurrentCertIndex(prev => prev === 0 ? certifications.length - 1 : prev - 1);

  useEffect(() => {
    const interval = setInterval(nextCertSlide, 5000);
    return () => clearInterval(interval);
  }, [currentCertIndex]);

  return (
    <div className="App">
      {/* Header, Hero y About iguales */}

      {/* SECCIÓN HERRAMIENTAS */}
      <section id="herramientas" className="section herramientas-section">
        <div className="container">
          <h2 className="section-title">Herramientas y Tecnologías</h2>
          <p className="section-subtitle">Todas las herramientas que domino en el ámbito de la ciberseguridad</p>

          <div className="herramientas-grid">
            {todasLasHerramientas.map(herramienta => (
              <div
                key={herramienta.id}
                className={`herramienta-card ${hoveredCard === herramienta.id ? 'hovered' : ''}`}
                onMouseEnter={() => handleMouseEnter(herramienta.id)}
                onMouseLeave={handleMouseLeave}
              >
                <div className="herramienta-content">
                  <div className="herramienta-icon-wrapper">
                    <img 
                      src={herramienta.icon} 
                      alt={herramienta.name} 
                      className="herramienta-icon" 
                    />
                    <div className="icon-glow"></div>
                  </div>
                  <h3 className="herramienta-name">{herramienta.name}</h3>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Certificaciones, Educación y Footer iguales */}
    </div>
  );
}

export default App;
