import React, { useState, useEffect, useRef } from 'react';
import { useLanguage } from './context/LanguageContext';
import LanguageSelector from './components/LanguageSelector';
import './App.css';

function App() {
  const { t } = useLanguage();
  const [showPhone, setShowPhone] = useState(false);
  const [showCopied, setShowCopied] = useState(false);
  const [showImageModal, setShowImageModal] = useState(false);
  const [currentCertIndex, setCurrentCertIndex] = useState(0);
  const [currentToolIndex, setCurrentToolIndex] = useState(0);
  const carouselRef = useRef(null);
  const toolsCarouselRef = useRef(null);
  const email = 'davidcigaran@gmail.com';

  const certifications = [
    { id: 1, title: 'eJPT', image: '/img/ejpt.png', expedition: '2026' },
    { id: 2, title: 'Cibersecurity Profile Recon', image: '/img/f3.png', expedition: '2025' },
    { id: 3, title: 'Burp Suite Certified', image: '/img/portswigger.png', expedition: '2026' },
    { id: 4, title: 'AWS', image: '/img/aws.png', expedition: '2022' }
  ];

  const educationHistory = [
    { id: 1, title: 'Educación Secundaria Obligatoria (E.S.O)', date: '2019', institution: 'Ntra.Sra del Rosario Dominicas Barakaldo' },
    { id: 2, title: 'Grado Medio Hostelería y Turismo', date: '2019-2021', institution: 'UPV/EHU Leioa' },
    { id: 3, title: 'Aprendizaje de metodologías similares a S.M.R (Sistemas Microinformáticos en Redes)', date: '2021-2022', institution: 'Campus Training Bilbao' },
    { id: 4, title: 'Administración de Sistemas Informáticos en Redes (A.S.I.R)', date: '2023-2025', institution: 'Centro de Estudios ALMI (Deusto)' },
    { id: 5, title: 'Bootcamp de Ciberseguridad', date: '2025-2026', institution: 'The Bridge' }
  ];

  const experienceData = [
    {
      id: 1,
      title: 'Proyectos de Laboratorio',
      period: '2024 - Presente',
      description: 'Diseño y ejecución de entornos de laboratorio para prácticas de pentesting, incluyendo configuraciones de redes, máquinas virtuales vulnerables y escenarios de ataque controlados.',
      technologies: ['Kali Linux', 'VirtualBox', 'DVWA', 'Metasploitable']
    },
    {
      id: 2,
      title: 'Formación Práctica en Ciberseguridad',
      period: '2025 - 2026',
      description: 'Participación activa en el bootcamp de The Bridge, desarrollando habilidades en análisis de vulnerabilidades, testing de penetración y metodologías de red team.',
      technologies: ['OWASP Top 10', 'Burp Suite', 'Nmap', 'Wireshark']
    },
    {
      id: 3,
      title: 'Administración de Sistemas',
      period: '2023 - 2025',
      description: 'Gestión y mantenimiento de infraestructuras informáticas durante el ciclo formativo de ASIR, incluyendo servidores Windows y Linux.',
      technologies: ['Windows Server', 'Ubuntu Server', 'Active Directory', 'Docker']
    }
  ];

  const renderHTML = (text) => {
    return { __html: text };
  };

  const currentYear = new Date().getFullYear();

  const handleCopyEmail = () => {
    navigator.clipboard.writeText(email);
    setShowCopied(true);
    setTimeout(() => {
      setShowCopied(false);
    }, 2000);
  };

  const nextCertSlide = () => {
    setCurrentCertIndex((prevIndex) => 
      prevIndex === certifications.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevCertSlide = () => {
    setCurrentCertIndex((prevIndex) => 
      prevIndex === 0 ? certifications.length - 1 : prevIndex - 1
    );
  };

  const nextToolSlide = () => {
    setCurrentToolIndex((prevIndex) => 
      prevIndex === 5 ? 0 : prevIndex + 1
    );
  };

  const prevToolSlide = () => {
    setCurrentToolIndex((prevIndex) => 
      prevIndex === 0 ? 5 : prevIndex - 1
    );
  };

  // Manejar clic en el carrusel
  const handleCarouselClick = (e) => {
    const container = e.currentTarget;
    const rect = container.getBoundingClientRect();
    const clickX = e.clientX - rect.left;
    const containerWidth = rect.width;

    if (clickX < containerWidth / 2) {
      prevToolSlide();
    } else {
      nextToolSlide();
    }
  };

  // Auto-rotate carousels
  useEffect(() => {
    const certInterval = setInterval(() => {
      nextCertSlide();
    }, 5000);

    return () => {
      clearInterval(certInterval);
    };
  }, [currentCertIndex]);

  return (
    <div className="App">
      <header className="header">
        <div className="language-selector-top-left">
          <LanguageSelector />
        </div>
<nav className="nav">
  <a href="/" className="logo">{t('nav.logo')}</a>
  <div className="nav-links">
    <a href="#about">{t('nav.about')}</a>
    <a href="#certifications">{t('nav.certifications')}</a>
    <a href="#experience">{t('nav.experience')}</a>
    <a href="#a_history">{t('nav.education')}</a>
    <a href="#habilidades">Herramientas</a>
    <div 
      className="contact-item" 
      onMouseEnter={() => setShowPhone(true)}
      onMouseLeave={() => setShowPhone(false)}
    >
      <a href="#contact" className="contact-link">
        {t('nav.contact')}
      </a>
      {showPhone && (
        <div className="phone-tooltip">
          <span className="phone-number">688 84 65 27</span>
          <div className="phone-tooltip-arrow"></div>
        </div>
      )}
    </div>
  </div>          <div className="nav-right">
            <div className="header-buttons">
              <a 
                href="https://www.youtube.com/@TuCanalDeYouTube" 
                target="_blank" 
                rel="noopener noreferrer" 
                className="project-link"
              >
                {t('nav.projects')}
              </a>
              <div className="social">
                <a 
                  href="https://linkedin.com/in/davidorregod" 
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  {t('hero.linkedin')}
                </a>
                <a 
                  href="https://github.com/davidorregod" 
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  {t('hero.github')}
                </a>
              </div>
            </div>
            <div className="language-selector">
              <LanguageSelector />
            </div>
          </div>
        </nav>
      </header>

      <section className="hero">
        <div className="hero-content">
          <div 
            className="hero-image" 
            onClick={() => setShowImageModal(true)}
            title="Click para ampliar"
          >
            <img 
              src="https://davidorrego.dev/images/profile-new.svg" 
              alt="Profile" 
              className="profile-img" 
            />
          </div>
          <div className="hero-text">
            <h1 dangerouslySetInnerHTML={renderHTML(t('hero.title'))} />
            <p className="tagline">{t('hero.tagline')}</p>
            <p className="description" dangerouslySetInnerHTML={renderHTML(t('hero.description'))} />
            <div className="hero-buttons">
              <a 
                href="https://linkedin.com/in/davidorregod" 
                className="btn"
              >
                {t('hero.linkedin')}
              </a>
              <a 
                href="https://youtube.com" 
                className="btn"
              >
                {t('hero.youtube')}
              </a>
              <button onClick={handleCopyEmail} className="btn email-btn">
                {t('hero.email')}
              </button>
            </div>
          </div>
        </div>
      </section>

      <section id="about" className="about-section">
        <div className="about-container">
          <div className="about-header">
            <h2>¿Quién Soy?</h2>
            <p className="about-subtitle">Aprendiz junior</p>
          </div>

          <div className="about-content">
            <div className="about-text">
              <p className="about-description">
                Mi nombre es David Cigarán Flores, tengo 23 años y recientemente he finalizado mis 
                estudios de Administración de Sistemas Informáticos en Red (ASIR) y el bootcamp de 
                Ciberseguridad en The Bridge, donde descubrí mi gusto por la seguridad ofensiva y 
                el análisis de vulnerabilidades, más específicamente en el ámbito del Red Team.
              </p>

              <p className="about-description">
                Durante mi formación he desarrollado una base sólida en redes, sistemas y metodologías 
                de pentesting, y sigo ampliando mis conocimientos por interés propio. Me considero una 
                persona responsable, con muchas ganas de aprender y con la capacidad de adaptarme 
                rápidamente a nuevos entornos. Disfruto trabajando en equipo, compartiendo ideas y 
                enfrentando retos técnicos que me permitan crecer.
              </p>

              <p className="about-description">
                Busco una oportunidad para iniciar mi carrera en un entorno donde pueda aprender de 
                expertos, enfrentar desafíos reales y aportar desde el primer día. Mi objetivo es seguir 
                mejorando poco a poco, reforzar mis habilidades y llegar a ser un buen profesional en 
                el mundo de la ciberseguridad ofensiva.
              </p>
            </div>
          </div>
        </div>
      </section>

      <section id="certifications" className="section certifications-section">
        <div className="container">
          <h2 className="section-title">Certificaciones e Insignias</h2>

          <div className="certifications-carousel" ref={carouselRef}>
            <div className="carousel-wrapper">
              <div className="carousel-track" style={{ transform: `translateX(-${currentCertIndex * 100}%)` }}>
                {[...certifications, ...certifications, ...certifications].map((cert, index) => (
                  <div key={`${cert.id}-${index}`} className="carousel-slide">
                    <div className="cert-card">
                      <img 
                        src={cert.image} 
                        alt={cert.title} 
                        className="cert-image"
                      />
                      <div className="cert-content">
                        <h3 className="cert-title">{cert.title}</h3>
                        <p className="cert-expedition">Expedición: {cert.expedition}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <button className="carousel-btn prev-btn" onClick={prevCertSlide}>
              ‹
            </button>
            <button className="carousel-btn next-btn" onClick={nextCertSlide}>
              ›
            </button>

            <div className="carousel-indicators">
              {certifications.map((_, index) => (
                <button
                  key={index}
                  className={`indicator ${index === currentCertIndex % certifications.length ? 'active' : ''}`}
                  onClick={() => setCurrentCertIndex(index)}
                />
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* NUEVA SECCIÓN EXPERIENCIA */}
      <section id="experience" className="section experience-section">
        <div className="container">
          <h2 className="section-title">Experiencia</h2>

          <div className="timeline">
            {experienceData.map((item, index) => (
              <div key={item.id} className={`timeline-item ${index % 2 === 0 ? 'left' : 'right'}`}>
                <div className="timeline-content">
                  <div className="timeline-header">
                    <h3 className="timeline-title">{item.title}</h3>
                    <span className="timeline-period">{item.period}</span>
                  </div>
                  <p className="timeline-description">{item.description}</p>
                  <div className="timeline-technologies">
                    {item.technologies.map((tech, techIndex) => (
                      <span key={techIndex} className="tech-tag">{tech}</span>
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* SECCIÓN HABILIDADES - CARRUSEL */}
      <section id="habilidades" className="section">
        <div className="container">
          <h2 className="section-title">Experiencia en:</h2>     

          {/* Instrucción para el carrusel */}
          <div className="carrusel-instruccion">
            (Haga click en los lados de la tarjeta para deslizar) 
          </div>

          <div 
            id="habilidades-carousel-container"
            onClick={handleCarouselClick}
            ref={toolsCarouselRef}
          >
            <div 
              id="habilidades-carousel" 
              style={{ 
                transform: `translateX(-${currentToolIndex * 100}%)`,
                transition: 'transform 0.5s ease'
              }}
            >
              {/* === 0. Sistemas Operativos === */}
              <div className="habilidades-carousel-item" style={{ opacity: currentToolIndex === 0 ? '1' : '0' }}>
                <h3>Sistemas Operativos</h3>
                <div className="skill-items">
                  <div className="item"><img src="/img/kali.png" alt="Kali"/><span>Kali</span></div>
                  <div className="item"><img src="/img/parrot.png" alt="Parrot"/><span>Parrot</span></div>
                  <div className="item"><img src="/img/ubu.png" alt="Ubuntu"/><span>Ubuntu && Ubuntu Server</span></div>
                  <div className="item"><img src="/img/w7.jpeg" alt="Windows 7"/><span>Win 7</span></div>
                  <div className="item"><img src="/img/w10.png" alt="Windows 10"/><span>Win 10</span></div>
                  <div className="item"><img src="/img/w_server.png" alt="Windows Server"/><span>Win Server</span></div>
                  <div className="item"><img src="/img/zorin.png" alt="Zorin"/><span>Zorin</span></div>
                  <div className="item"><img src="/img/tails.png" alt="Tails"/><span>Tails</span></div>
                </div>
              </div>

              {/* === 1. OSINT (Inteligencia de Fuentes Abiertas) === */}
              <div className="habilidades-carousel-item" style={{ opacity: currentToolIndex === 1 ? '1' : '0' }}>
                <h3>OSINT</h3>
                <div className="skill-items">
                  <div className="item"><img src="/img/Dorks.webp" alt="Google Dorks"/><span>Google Dorks</span></div>
                  <div className="item"><img src="/img/cen.png" alt="Censys"/><span>Censys</span></div>
                  <div className="item"><img src="/img/shodan.jpeg" alt="Shodan"/><span>Shodan</span></div>
                  <div className="item"><img src="/img/spiderfoot.png" alt="SpiderFoot"/><span>SpiderFoot</span></div>
                  <div className="item"><img src="/img/FOCA.png" alt="FOCA"/><span>FOCA</span></div>
                  <div className="item"><img src="/img/maltego.png" alt="Maltego"/><span>Maltego</span></div>
                  <div className="item"><img src="/img/httrack.png" alt="HTTrack"/><span>HTTrack</span></div>
                  <div className="item"><img src="/img/exiftool.png" alt="ExifTool"/><span>ExifTool</span></div>
                </div>
              </div>

              {/* === 2. Escaneo Web y Fuzzers === */}
              <div className="habilidades-carousel-item" style={{ opacity: currentToolIndex === 2 ? '1' : '0' }}>
                <h3>Escaneo Web y Fuzzing</h3>
                <div className="skill-items">
                  <div className="item"><img src="/img/nikto.jpeg" alt="Nikto"/><span>Nikto</span></div>
                  <div className="item"><img src="/img/dirb.png" alt="Dirb"/><span>Dirb</span></div>
                  <div className="item"><img src="/img/dirbuster.png" alt="DirBuster"/><span>DirBuster</span></div>
                  <div className="item"><img src="/img/dirsearch.png" alt="DirSearch"/><span>DirSearch</span></div>
                  <div className="item"><img src="/img/gobuster.png" alt="GoBuster"/><span>GoBuster</span></div>
                  <div className="item"><img src="/img/Burp_suite.png" alt="Burp Suite"/><span>Burp Suite</span></div>
                  <div className="item"><img src="/img/sqlmap.png" alt="SQLMap"/><span>SQLMap</span></div>
                </div>
              </div>

              {/* === 3. Análisis de Red === */}
              <div className="habilidades-carousel-item" style={{ opacity: currentToolIndex === 3 ? '1' : '0' }}>
                <h3>Análisis de Red</h3>
                <div className="skill-items">
                  <div className="item"><img src="/img/nmap.jpeg" alt="Nmap"/><span>Nmap</span></div>
                  <div className="item"><img src="/img/arp.png" alt="ARP-Scan"/><span>ARP-Scan</span></div>
                  <div className="item"><img src="/img/ping.jpeg" alt="Ping"/><span>Ping</span></div>
                  <div className="item"><img src="/img/curl.png" alt="cURL"/><span>cURL</span></div>
                  <div className="item"><img src="/img/netcat.png" alt="Netcat"/><span>Netcat</span></div>
                  <div className="item"><img src="/img/wireshark.jpeg" alt="Wireshark"/><span>Wireshark</span></div>
                  <div className="item"><img src="/img/OpenSSL.jpg" alt="OpenSSL"/><span>OpenSSL</span></div>
                </div>
              </div>

              {/* === 4. Escáneres de Vulnerabilidades === */}
              <div className="habilidades-carousel-item" style={{ opacity: currentToolIndex === 4 ? '1' : '0' }}>
                <h3>Escaneo y Virtualización</h3>
                <div className="skill-items">
                  <div className="item"><img src="/img/nessus.png" alt="Nessus"/><span>Nessus</span></div>
                  <div className="item"><img src="/img/greenbone.png" alt="Greenbone"/><span>Greenbone</span></div>
                  <div className="item"><img src="/img/dvwa.jpeg" alt="DVWA"/><span>DVWA</span></div>
                  <div className="item"><img src="/img/aws.png" alt="AWS"/><span>AWS</span></div>
                  <div className="item"><img src="/img/vbox.png" alt="VirtualBox"/><span>VirtualBox</span></div>
                </div>
              </div>

              {/* === 5. Ataque y Explotación === */}
              <div className="habilidades-carousel-item" style={{ opacity: currentToolIndex === 5 ? '1' : '0' }}>
                <h3>Control y Explotación</h3>
                <div className="skill-items">
                  <div className="item"><img src="/img/hidra.png" alt="Hydra"/><span>Hydra</span></div>
                  <div className="item"><img src="/img/john.png" alt="John"/><span>John</span></div>
                  <div className="item"><img src="/img/hash.jpeg" alt="Hash-Identifier"/><span>Hash-Identifier</span></div>
                  <div className="item"><img src="/img/decode.png" alt="Decode"/><span>Decode</span></div>
                  <div className="item"><img src="/img/meta.png" alt="Metasploit"/><span>Metasploit</span></div>
                  <div className="item"><img src="/img/bash.png" alt="Bash"/><span>Bash</span></div>
                  <div className="item"><img src="/img/mysql.png" alt="MySQL"/><span>MySQL</span></div>
                </div>
              </div>
            </div>
          </div>

          {/* Indicadores */}
          <div className="carousel-indicators">
            {[0, 1, 2, 3, 4, 5].map((index) => (
              <button
                key={index}
                className={`carousel-indicator ${index === currentToolIndex ? 'active' : ''}`}
                onClick={(e) => {
                  e.stopPropagation();
                  setCurrentToolIndex(index);
                }}
              />
            ))}
          </div>
        </div>
      </section>

      <section id="a_history" className="section education-section">
        <div className="container">
          <h2 className="section-title education-title">Historial Académico</h2>

          <div className="timeline">
            {educationHistory.map((item, index) => (
              <div key={item.id} className="timeline-item">
                <div className="timeline-content">
                  <h3 className="timeline-title">{item.title}</h3>
                  <div className="timeline-date">{item.date}</div>
                  <p className="timeline-institution">{item.institution}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {showImageModal && (
        <div 
          className="modal-overlay"
          onClick={() => setShowImageModal(false)}
        >
          <img 
            src="https://davidorrego.dev/images/profile-new.svg" 
            alt="Profile ampliada" 
            className="modal-image"
            onClick={(e) => e.stopPropagation()}
          />
          <button 
            className="modal-close-btn"
            onClick={() => setShowImageModal(false)}
            aria-label="Cerrar imagen"
          >
            ✕
          </button>
        </div>
      )}

      {showCopied && (
        <div className="copy-toast">
          📋 Email copiado al portapapeles
        </div>
      )}

      <footer className="footer">
        <p>{t('footer.inspired')}</p>
        <p>
          {t('footer.copyright')
            .replace('[AÑO]', currentYear)
            .replace('[TU NOMBRE]', 'David Cigarán')}
        </p>
      </footer>
      <div className="background-effect"></div>
    </div>
  );
}

export default App;
