import React, { useState, useEffect, useRef } from 'react';
import { useLanguage } from './context/LanguageContext';
import LanguageSelector from './components/LanguageSelector';
import './App.css';

function App() {
  const { t } = useLanguage();
  const [showPhone, setShowPhone] = useState(false);
  const [showCopied, setShowCopied] = useState(false);
  const [showImageModal, setShowImageModal] = useState(false);
  const [currentCertIndex, setCurrentCertIndex] = useState(0);
  const [currentToolIndex, setCurrentToolIndex] = useState(0);

  const carouselRef = useRef(null);
  const toolsCarouselRef = useRef(null);

  /* ===== EXPERIENCE SLIDER REF ===== */
  const experienceSliderRef = useRef(null);

  const email = 'davidcigaran@gmail.com';

  /* ===== EXPERIENCE SLIDER FUNCTION ===== */
  const slideExperience = (direction) => {
    const slider = experienceSliderRef.current;
    const cardWidth = slider.firstChild.offsetWidth + 32;

    slider.scrollBy({
      left: direction === 'left' ? -cardWidth : cardWidth,
      behavior: 'smooth'
    });
  };

  const certifications = [
    { id: 1, title: 'eJPT', image: '/img/ejpt.png', expedition: '2026' },
    { id: 2, title: 'Cibersecurity Profile Recon', image: '/img/f3.png', expedition: '2025' },
    { id: 3, title: 'Burp Suite Certified', image: '/img/portswigger.png', expedition: '2026' },
    { id: 4, title: 'AWS', image: '/img/aws.png', expedition: '2022' }
  ];

  const educationHistory = [
    { id: 1, title: 'Educaci√≥n Secundaria Obligatoria (E.S.O)', date: '2019', institution: 'Ntra.Sra del Rosario Dominicas Barakaldo' },
    { id: 2, title: 'Grado Medio Hosteler√≠a y Turismo', date: '2019-2021', institution: 'UPV/EHU Leioa' },
    { id: 3, title: 'Aprendizaje de metodolog√≠as similares a S.M.R', date: '2021-2022', institution: 'Campus Training Bilbao' },
    { id: 4, title: 'ASIR', date: '2023-2025', institution: 'Centro de Estudios ALMI (Deusto)' },
    { id: 5, title: 'Bootcamp Ciberseguridad', date: '2025-2026', institution: 'The Bridge' }
  ];

  const renderHTML = (text) => ({ __html: text });
  const currentYear = new Date().getFullYear();

  const handleCopyEmail = () => {
    navigator.clipboard.writeText(email);
    setShowCopied(true);
    setTimeout(() => setShowCopied(false), 2000);
  };

  const nextCertSlide = () => {
    setCurrentCertIndex((prev) => prev === certifications.length - 1 ? 0 : prev + 1);
  };

  const prevCertSlide = () => {
    setCurrentCertIndex((prev) => prev === 0 ? certifications.length - 1 : prev - 1);
  };

  const nextToolSlide = () => {
    setCurrentToolIndex((prev) => (prev === 5 ? 0 : prev + 1));
  };

  const prevToolSlide = () => {
    setCurrentToolIndex((prev) => (prev === 0 ? 5 : prev - 1));
  };

  const handleCarouselClick = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    e.clientX - rect.left < rect.width / 2 ? prevToolSlide() : nextToolSlide();
  };

  useEffect(() => {
    const certInterval = setInterval(nextCertSlide, 5000);
    return () => clearInterval(certInterval);
  }, []);

  return (
    <div className="App">

      {/* ================= EXPERIENCE SLIDER ================= */}
      <section id="experience" className="section experience-section">
        <div className="container">
          <h2 className="section-title">Experiencia Profesional</h2>

          <div className="experience-slider">
            <button className="slider-btn left" onClick={() => slideExperience('left')}>‚Äπ</button>

            <div className="slider-viewport">
              <div className="slider-track" ref={experienceSliderRef}>

                {/* CARD 1 */}
                <div className="experience-card highlight">
                  <div className="experience-header">
                    <div className="experience-icon">üíº</div>
                    <div>
                      <h3 className="experience-card-title">Centro de Estudios ALMI</h3>
                      <p className="experience-subtitle">Pr√°cticas Profesionales</p>
                      <span className="experience-period">Feb 2025 ‚Äì Mayo 2025</span>
                    </div>
                  </div>

                  <div className="experience-body">
                    <p className="experience-intro">
                      Desarrollo y adaptaci√≥n de la web del Centro de D√≠a de Basurto.
                    </p>

                    <ul className="experience-tasks">
                      <li>üì§ Importaci√≥n y exportaci√≥n del sitio</li>
                      <li>üóÑ Gesti√≥n de bases de datos</li>
                      <li>üîå Optimizaci√≥n de plugins</li>
                      <li>üìù Documentaci√≥n t√©cnica</li>
                    </ul>

                    <div className="tech-tags">
                      <span>WordPress</span>
                      <span>MySQL</span>
                      <span>PHP</span>
                      <span>HTML</span>
                      <span>CSS</span>
                      <span>JavaScript</span>
                    </div>
                  </div>
                </div>

                {/* CARD 2 */}
                <div className="experience-card">
                  <div className="experience-header">
                    <div className="experience-icon">üéì</div>
                    <div>
                      <h3 className="experience-card-title">Proyectos Acad√©micos</h3>
                      <p className="experience-subtitle">The Bridge & ASIR</p>
                      <span className="experience-period">2023 ‚Äì 2026</span>
                    </div>
                  </div>

                  <div className="experience-body">
                    <p className="experience-intro">
                      Proyectos pr√°cticos de ciberseguridad y sistemas.
                    </p>

                    <ul>
                      <li>Pentesting</li>
                      <li>Administraci√≥n Windows/Linux</li>
                      <li>Laboratorios de red</li>
                    </ul>
                  </div>
                </div>

              </div>
            </div>

            <button className="slider-btn right" onClick={() => slideExperience('right')}>‚Ä∫</button>
          </div>
        </div>
      </section>

      <footer className="footer">
        <p>¬© {currentYear} David Cigar√°n</p>
      </footer>

    </div>
  );
}

export default App;
