import React, { useState, useEffect, useRef } from 'react';
import { useLanguage } from './context/LanguageContext';
import LanguageSelector from './components/LanguageSelector';
import './App.css';

function App() {
  const { t } = useLanguage();
  const [showPhone, setShowPhone] = useState(false);
  const [showCopied, setShowCopied] = useState(false);
  const [showImageModal, setShowImageModal] = useState(false);
  const [currentCertIndex, setCurrentCertIndex] = useState(0);
  const [hoveredCard, setHoveredCard] = useState(null);
  const carouselRef = useRef(null);
  const email = 'davidcigaran@gmail.com';

  // Todas las herramientas en una sola lista
  const todasLasHerramientas = [
    // Sistemas Operativos
    { id: 1, name: 'Kali', icon: 'img/kali.png' },
    { id: 2, name: 'Parrot', icon: 'img/parrot.png' },
    { id: 3, name: 'Ubuntu && Ubuntu Server', icon: 'img/ubu.png' },
    { id: 4, name: 'Windows 7', icon: 'img/w7.jpeg' },
    { id: 5, name: 'Windows 10', icon: 'img/w10.png' },
    { id: 6, name: 'Windows Server', icon: 'img/w_server.png' },
    { id: 7, name: 'Zorin', icon: 'img/zorin.png' },
    { id: 8, name: 'Tails', icon: 'img/tails.png' },

    // OSINT
    { id: 9, name: 'Google Dorks', icon: 'img/Dorks.webp' },
    { id: 10, name: 'Censys', icon: 'img/cen.png' },
    { id: 11, name: 'Shodan', icon: 'img/shodan.jpeg' },
    { id: 12, name: 'SpiderFoot', icon: 'img/spiderfoot.png' },
    { id: 13, name: 'FOCA', icon: 'img/FOCA.png' },
    { id: 14, name: 'Maltego', icon: 'img/maltego.png' },
    { id: 15, name: 'HTTrack', icon: 'img/httrack.png' },
    { id: 16, name: 'ExifTool', icon: 'img/exiftool.png' },

    // Escaneo Web y Fuzzing
    { id: 17, name: 'Nikto', icon: 'img/nikto.jpeg' },
    { id: 18, name: 'Dirb', icon: 'img/dirb.png' },
    { id: 19, name: 'DirBuster', icon: 'img/dirbuster.png' },
    { id: 20, name: 'DirSearch', icon: 'img/dirsearch.png' },
    { id: 21, name: 'GoBuster', icon: 'img/gobuster.png' },
    { id: 22, name: 'Burp Suite', icon: 'img/Burp_suite.png' },
    { id: 23, name: 'SQLMap', icon: 'img/sqlmap.png' },

    // Análisis de Red
    { id: 24, name: 'Nmap', icon: 'img/nmap.jpeg' },
    { id: 25, name: 'ARP-Scan', icon: 'img/arp.png' },
    { id: 26, name: 'Ping', icon: 'img/ping.jpeg' },
    { id: 27, name: 'cURL', icon: 'img/curl.png' },
    { id: 28, name: 'Netcat', icon: 'img/netcat.png' },
    { id: 29, name: 'Wireshark', icon: 'img/wireshark.jpeg' },
    { id: 30, name: 'OpenSSL', icon: 'img/OpenSSL.jpg' },

    // Escaneo y Virtualización
    { id: 31, name: 'Nessus', icon: 'img/nessus.png' },
    { id: 32, name: 'Greenbone', icon: 'img/greenbone.png' },
    { id: 33, name: 'DVWA', icon: 'img/dvwa.jpeg' },
    { id: 34, name: 'AWS', icon: 'img/aws.png' },
    { id: 35, name: 'VirtualBox', icon: 'img/vbox.png' },

    // Control y Explotación
    { id: 36, name: 'Hydra', icon: 'img/hidra.png' },
    { id: 37, name: 'John', icon: 'img/john.png' },
    { id: 38, name: 'Hash-Identifier', icon: 'img/hash.jpeg' },
    { id: 39, name: 'Decode', icon: 'img/decode.png' },
    { id: 40, name: 'Metasploit', icon: 'img/meta.png' },
    { id: 41, name: 'Bash', icon: 'img/bash.png' },
    { id: 42, name: 'MySQL', icon: 'img/mysql.png' }
  ];

  const certifications = [
    {
      id: 1,
      title: 'eJPT',
      image: 'img/ejpt.png',
      expedition: '2026'
    },
    {
      id: 2,
      title: 'Cibersecurity Profile Recon',
      image: 'img/f3.png',
      expedition: '2025'
    },
    {
      id: 3,
      title: 'Burp Suite Certified',
      image: 'img/portswigger.png',
      expedition: '2026'
    },
    {
      id: 4,
      title: 'AWS',
      image: 'img/aws.png',
      expedition: '2022'
    }
  ];

  const educationHistory = [
    {
      id: 1,
      title: 'Educación Secundaria Obligatoria (E.S.O)',
      date: '2019',
      institution: 'Ntra.Sra del Rosario Dominicas Barakaldo'
    },
    {
      id: 2,
      title: 'Grado Medio Hostelería y Turismo',
      date: '2019-2021',
      institution: 'UPV/EHU Leioa'
    },
    {
      id: 3,
      title: 'Aprendizaje de metodologías similares a S.M.R (Sistemas Microinformáticos en Redes)',
      date: '2021-2022',
      institution: 'Campus Training Bilbao'
    },
    {
      id: 4,
      title: 'Administración de Sistemas Informáticos en Redes (A.S.I.R)',
      date: '2023-2025',
      institution: 'Centro de Estudios ALMI (Deusto)'
    },
    {
      id: 5,
      title: 'Bootcamp de Ciberseguridad',
      date: '2025-2026',
      institution: 'The Bridge'
    }
  ];

  const renderHTML = (text) => {
    return { __html: text };
  };

  const currentYear = new Date().getFullYear();

  const handleCopyEmail = () => {
    navigator.clipboard.writeText(email);
    setShowCopied(true);
    setTimeout(() => {
      setShowCopied(false);
    }, 2000);
  };

  const nextCertSlide = () => {
    setCurrentCertIndex((prevIndex) => 
      prevIndex === certifications.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevCertSlide = () => {
    setCurrentCertIndex((prevIndex) => 
      prevIndex === 0 ? certifications.length - 1 : prevIndex - 1
    );
  };

  // Efecto hover para las tarjetas
  const handleMouseEnter = (id) => {
    setHoveredCard(id);
  };

  const handleMouseLeave = () => {
    setHoveredCard(null);
  };

  // Auto-rotate carousel de certificaciones
  useEffect(() => {
    const certInterval = setInterval(() => {
      nextCertSlide();
    }, 5000);

    return () => {
      clearInterval(certInterval);
    };
  }, [currentCertIndex]);

  return (
    <div className="App">
      {/* Header */}
      <header className="header">
        <div className="language-selector-top-left">
          <LanguageSelector />
        </div>
        <nav className="nav">
          <a href="/" className="logo">{t('nav.logo')}</a>
          <div className="nav-links">
            <a href="#certifications">{t('nav.certifications')}</a>
            <a href="#experience">{t('nav.experience')}</a>
            <a href="#about">{t('nav.about')}</a>
            <a href="#herramientas">Herramientas</a>
            <a href="#a_history">{t('nav.education')}</a>
            <div 
              className="contact-item" 
              onMouseEnter={() => setShowPhone(true)}
              onMouseLeave={() => setShowPhone(false)}
            >
              <a href="#contact" className="contact-link">
                {t('nav.contact')}
              </a>
              {showPhone && (
                <div className="phone-tooltip">
                  <span className="phone-number">688 84 65 27</span>
                  <div className="phone-tooltip-arrow"></div>
                </div>
              )}
            </div>
          </div>
          <div className="nav-right">
            <div className="header-buttons">
              <a 
                href="https://www.youtube.com/@TuCanalDeYouTube" 
                target="_blank" 
                rel="noopener noreferrer" 
                className="project-link"
              >
                {t('nav.projects')}
              </a>
              <div className="social">
                <a 
                  href="https://linkedin.com/in/davidorregod" 
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  {t('hero.linkedin')}
                </a>
                <a 
                  href="https://github.com/davidorregod" 
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  {t('hero.github')}
                </a>
              </div>
            </div>
            <div className="language-selector">
              <LanguageSelector />
            </div>
          </div>
        </nav>
      </header>

      {/* Hero */}
      <section className="hero">
        <div className="hero-content">
          <div 
            className="hero-image" 
            onClick={() => setShowImageModal(true)}
            title="Click para ampliar"
          >
            <img 
              src="https://davidorrego.dev/images/profile-new.svg" 
              alt="Profile" 
              className="profile-img" 
            />
          </div>
          <div className="hero-text">
            <h1 dangerouslySetInnerHTML={renderHTML(t('hero.title'))} />
            <p className="tagline">{t('hero.tagline')}</p>
            <p className="description" dangerouslySetInnerHTML={renderHTML(t('hero.description'))} />
            <div className="hero-buttons">
              <a 
                href="https://linkedin.com/in/davidorregod" 
                className="btn"
              >
                {t('hero.linkedin')}
              </a>
              <a 
                href="https://youtube.com" 
                className="btn"
              >
                {t('hero.youtube')}
              </a>
              <button onClick={handleCopyEmail} className="btn email-btn">
                {t('hero.email')}
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* SECCIÓN SOBRE MÍ */}
      <section id="about" className="about-section">
        <div className="about-container">
          <div className="about-header">
            <h2>¿Quién Soy?</h2>
            <p className="about-subtitle">Aprendiz junior</p>
          </div>

          <div className="about-content">
            <div className="about-text">
              <p className="about-description">
                Mi nombre es David Cigarán Flores, tengo 23 años y recientemente he finalizado mis 
                estudios de Administración de Sistemas Informáticos en Red (ASIR) y el bootcamp de 
                Ciberseguridad en The Bridge, donde descubrí mi gusto por la seguridad ofensiva y 
                el análisis de vulnerabilidades, más específicamente en el ámbito del Red Team.
              </p>

              <p className="about-description">
                Durante mi formación he desarrollado una base sólida en redes, sistemas y metodologías 
                de pentesting, y sigo ampliando mis conocimientos por interés propio. Me considero una 
                persona responsable, con muchas ganas de aprender y con la capacidad de adaptarme 
                rápidamente a nuevos entornos. Disfruto trabajando en equipo, compartiendo ideas y 
                enfrentando retos técnicos que me permitan crecer.
              </p>

              <p className="about-description">
                Busco una oportunidad para iniciar mi carrera en un entorno donde pueda aprender de 
                expertos, enfrentar desafíos reales y aportar desde el primer día. Mi objetivo es seguir 
                mejorando poco a poco, reforzar mis habilidades y llegar a ser un buen profesional en 
                el mundo de la ciberseguridad ofensiva.
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* SECCIÓN HERRAMIENTAS - TODAS JUNTAS */}
      <section id="herramientas" className="section herramientas-section">
        <div className="container">
          <h2 className="section-title">Herramientas y Tecnologías</h2>
          <p className="section-subtitle">Todas las herramientas que domino en el ámbito de la ciberseguridad</p>

          <div className="herramientas-grid">
            {todasLasHerramientas.map((herramienta) => (
              <div 
                key={herramienta.id}
                className={`herramienta-card ${hoveredCard === herramienta.id ? 'hovered' : ''}`}
                onMouseEnter={() => handleMouseEnter(herramienta.id)}
                onMouseLeave={handleMouseLeave}
              >
                {/* Efecto de partículas */}
                <div className="particle-effect">
                  {[...Array(8)].map((_, i) => (
                    <div key={i} className="particle"></div>
                  ))}
                </div>

                {/* Efecto de borde energético */}
                <div className="energy-border"></div>

                {/* Contenido de la tarjeta */}
                <div className="herramienta-content">
                  <div className="herramienta-icon-wrapper">
                    <img 
                      src={herramienta.icon} 
                      alt={herramienta.name}
                      className="herramienta-icon"
                    />
                    <div className="icon-glow"></div>
                  </div>
                  <h3 className="herramienta-name">{herramienta.name}</h3>
                  <div className="herramienta-glow"></div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* SECCIÓN CERTIFICACIONES */}
      <section id="certifications" className="section certifications-section">
        <div className="container">
          <h2 className="section-title">Certificaciones e Insignias</h2>

          <div className="certifications-carousel" ref={carouselRef}>
            <div className="carousel-wrapper">
              <div className="carousel-track" style={{ transform: `translateX(-${currentCertIndex * 100}%)` }}>
                {[...certifications, ...certifications, ...certifications].map((cert, index) => (
                  <div key={`${cert.id}-${index}`} className="carousel-slide">
                    <div className="cert-card">
                      <img 
                        src={cert.image} 
                        alt={cert.title} 
                        className="cert-image"
                      />
                      <div className="cert-content">
                        <h3 className="cert-title">{cert.title}</h3>
                        <p className="cert-expedition">Expedición: {cert.expedition}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <button className="carousel-btn prev-btn" onClick={prevCertSlide}>
              ‹
            </button>
            <button className="carousel-btn next-btn" onClick={nextCertSlide}>
              ›
            </button>

            <div className="carousel-indicators">
              {certifications.map((_, index) => (
                <button
                  key={index}
                  className={`indicator ${index === currentCertIndex % certifications.length ? 'active' : ''}`}
                  onClick={() => setCurrentCertIndex(index)}
                />
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* SECCIÓN HISTORIAL ACADÉMICO */}
      <section id="a_history" className="section education-section">
        <div className="container">
          <h2 className="section-title education-title">Historial Académico</h2>

          <div className="timeline">
            {educationHistory.map((item, index) => (
              <div key={item.id} className="timeline-item">
                <div className="timeline-content">
                  <h3 className="timeline-title">{item.title}</h3>
                  <div className="timeline-date">{item.date}</div>
                  <p className="timeline-institution">{item.institution}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Modal para imagen ampliada */}
      {showImageModal && (
        <div 
          className="modal-overlay"
          onClick={() => setShowImageModal(false)}
        >
          <img 
            src="https://davidorrego.dev/images/profile-new.svg" 
            alt="Profile ampliada" 
            className="modal-image"
            onClick={(e) => e.stopPropagation()}
          />
          <button 
            className="modal-close-btn"
            onClick={() => setShowImageModal(false)}
            aria-label="Cerrar imagen"
          >
            ✕
          </button>
        </div>
      )}

      {/* Toast bonito */}
      {showCopied && (
        <div className="copy-toast">
          📋 Email copiado al portapapeles
        </div>
      )}

      {/* Footer */}
      <footer className="footer">
        <p>{t('footer.inspired')}</p>
        <p>
          {t('footer.copyright')
            .replace('[AÑO]', currentYear)
            .replace('[TU NOMBRE]', 'David Cigarán')}
        </p>
      </footer>
      <div className="background-effect"></div>
    </div>
  );
}

export default App;
