import React, { useState, useEffect, useRef } from 'react';
import { useLanguage } from './context/LanguageContext';
import LanguageSelector from './components/LanguageSelector';
import './App.css';

// Importar imágenes desde la carpeta img
import kaliImg from './img/kali.png';
import parrotImg from './img/parrot.png';
import ubuImg from './img/ubu.png';
import w7Img from './img/w7.jpeg';
import w10Img from './img/w10.png';
import wServerImg from './img/w_server.png';
import zorinImg from './img/zorin.png';
import tailsImg from './img/tails.png';
import dorksImg from './img/Dorks.webp';
import censysImg from './img/cen.png';
import shodanImg from './img/shodan.jpeg';
import spiderfootImg from './img/spiderfoot.png';
import focaImg from './img/FOCA.png';
import maltegoImg from './img/maltego.png';
import httrackImg from './img/httrack.png';
import exiftoolImg from './img/exiftool.png';
import niktoImg from './img/nikto.jpeg';
import dirbImg from './img/dirb.png';
import dirbusterImg from './img/dirbuster.png';
import dirsearchImg from './img/dirsearch.png';
import gobusterImg from './img/gobuster.png';
import burpImg from './img/Burp_suite.png';
import sqlmapImg from './img/sqlmap.png';
import nmapImg from './img/nmap.jpeg';
import arpImg from './img/arp.png';
import pingImg from './img/ping.jpeg';
import curlImg from './img/curl.png';
import netcatImg from './img/netcat.png';
import wiresharkImg from './img/wireshark.jpeg';
import opensslImg from './img/OpenSSL.jpg';
import nessusImg from './img/nessus.png';
import greenboneImg from './img/greenbone.png';
import dvwaImg from './img/dvwa.jpeg';
import awsImg from './img/aws.png';
import vboxImg from './img/vbox.png';
import hydraImg from './img/hidra.png';
import johnImg from './img/john.png';
import hashImg from './img/hash.jpeg';
import decodeImg from './img/decode.png';
import metasploitImg from './img/meta.png';
import bashImg from './img/bash.png';
import mysqlImg from './img/mysql.png';

// Certificaciones
import ejptImg from './img/ejpt.png';
import f3Img from './img/f3.png';
import portsImg from './img/portswigger.png';
import awsCertImg from './img/aws.png';

// Imagen del perfil
import profileImg from './img/profile-new.svg';

function App() {
  const { t } = useLanguage();
  const [showPhone, setShowPhone] = useState(false);
  const [showCopied, setShowCopied] = useState(false);
  const [showImageModal, setShowImageModal] = useState(false);
  const [currentCertIndex, setCurrentCertIndex] = useState(0);
  const [hoveredCard, setHoveredCard] = useState(null);
  const carouselRef = useRef(null);
  const email = 'davidcigaran@gmail.com';

  // Lista completa de herramientas
  const todasLasHerramientas = [
    { id: 1, name: 'Kali', icon: kaliImg },
    { id: 2, name: 'Parrot', icon: parrotImg },
    { id: 3, name: 'Ubuntu && Ubuntu Server', icon: ubuImg },
    { id: 4, name: 'Windows 7', icon: w7Img },
    { id: 5, name: 'Windows 10', icon: w10Img },
    { id: 6, name: 'Windows Server', icon: wServerImg },
    { id: 7, name: 'Zorin', icon: zorinImg },
    { id: 8, name: 'Tails', icon: tailsImg },
    { id: 9, name: 'Google Dorks', icon: dorksImg },
    { id: 10, name: 'Censys', icon: censysImg },
    { id: 11, name: 'Shodan', icon: shodanImg },
    { id: 12, name: 'SpiderFoot', icon: spiderfootImg },
    { id: 13, name: 'FOCA', icon: focaImg },
    { id: 14, name: 'Maltego', icon: maltegoImg },
    { id: 15, name: 'HTTrack', icon: httrackImg },
    { id: 16, name: 'ExifTool', icon: exiftoolImg },
    { id: 17, name: 'Nikto', icon: niktoImg },
    { id: 18, name: 'Dirb', icon: dirbImg },
    { id: 19, name: 'DirBuster', icon: dirbusterImg },
    { id: 20, name: 'DirSearch', icon: dirsearchImg },
    { id: 21, name: 'GoBuster', icon: gobusterImg },
    { id: 22, name: 'Burp Suite', icon: burpImg },
    { id: 23, name: 'SQLMap', icon: sqlmapImg },
    { id: 24, name: 'Nmap', icon: nmapImg },
    { id: 25, name: 'ARP-Scan', icon: arpImg },
    { id: 26, name: 'Ping', icon: pingImg },
    { id: 27, name: 'cURL', icon: curlImg },
    { id: 28, name: 'Netcat', icon: netcatImg },
    { id: 29, name: 'Wireshark', icon: wiresharkImg },
    { id: 30, name: 'OpenSSL', icon: opensslImg },
    { id: 31, name: 'Nessus', icon: nessusImg },
    { id: 32, name: 'Greenbone', icon: greenboneImg },
    { id: 33, name: 'DVWA', icon: dvwaImg },
    { id: 34, name: 'AWS', icon: awsImg },
    { id: 35, name: 'VirtualBox', icon: vboxImg },
    { id: 36, name: 'Hydra', icon: hydraImg },
    { id: 37, name: 'John', icon: johnImg },
    { id: 38, name: 'Hash-Identifier', icon: hashImg },
    { id: 39, name: 'Decode', icon: decodeImg },
    { id: 40, name: 'Metasploit', icon: metasploitImg },
    { id: 41, name: 'Bash', icon: bashImg },
    { id: 42, name: 'MySQL', icon: mysqlImg }
  ];

  // Certificaciones
  const certifications = [
    { id: 1, title: 'eJPT', image: ejptImg, expedition: '2026' },
    { id: 2, title: 'Cibersecurity Profile Recon', image: f3Img, expedition: '2025' },
    { id: 3, title: 'Burp Suite Certified', image: portsImg, expedition: '2026' },
    { id: 4, title: 'AWS', image: awsCertImg, expedition: '2022' }
  ];

  const educationHistory = [
    { id: 1, title: 'Educación Secundaria Obligatoria (E.S.O)', date: '2019', institution: 'Ntra.Sra del Rosario Dominicas Barakaldo' },
    { id: 2, title: 'Grado Medio Hostelería y Turismo', date: '2019-2021', institution: 'UPV/EHU Leioa' },
    { id: 3, title: 'Aprendizaje de metodologías similares a S.M.R (Sistemas Microinformáticos en Redes)', date: '2021-2022', institution: 'Campus Training Bilbao' },
    { id: 4, title: 'Administración de Sistemas Informáticos en Redes (A.S.I.R)', date: '2023-2025', institution: 'Centro de Estudios ALMI (Deusto)' },
    { id: 5, title: 'Bootcamp de Ciberseguridad', date: '2025-2026', institution: 'The Bridge' }
  ];

  const currentYear = new Date().getFullYear();

  const handleCopyEmail = () => {
    navigator.clipboard.writeText(email);
    setShowCopied(true);
    setTimeout(() => setShowCopied(false), 2000);
  };

  const handleMouseEnter = (id) => setHoveredCard(id);
  const handleMouseLeave = () => setHoveredCard(null);

  const nextCertSlide = () => setCurrentCertIndex(prev => prev === certifications.length - 1 ? 0 : prev + 1);
  const prevCertSlide = () => setCurrentCertIndex(prev => prev === 0 ? certifications.length - 1 : prev - 1);

  useEffect(() => {
    const interval = setInterval(() => nextCertSlide(), 5000);
    return () => clearInterval(interval);
  }, []);

  const renderHTML = (text) => ({ __html: text });

  return (
    <div className="App">
      {/* Header */}
      <header className="header">
        <div className="language-selector-top-left"><LanguageSelector /></div>
        <nav className="nav">
          <a href="/" className="logo">{t('nav.logo')}</a>
          <div className="nav-links">
            <a href="#certifications">{t('nav.certifications')}</a>
            <a href="#experience">{t('nav.experience')}</a>
            <a href="#about">{t('nav.about')}</a>
            <a href="#herramientas">Herramientas</a>
            <a href="#a_history">{t('nav.education')}</a>
            <div className="contact-item" onMouseEnter={() => setShowPhone(true)} onMouseLeave={() => setShowPhone(false)}>
              <a href="#contact" className="contact-link">{t('nav.contact')}</a>
              {showPhone && (
                <div className="phone-tooltip">
                  <span className="phone-number">688 84 65 27</span>
                  <div className="phone-tooltip-arrow"></div>
                </div>
              )}
            </div>
          </div>
          <div className="nav-right">
            <div className="header-buttons">
              <a href="https://www.youtube.com/@TuCanalDeYouTube" target="_blank" rel="noopener noreferrer" className="project-link">{t('nav.projects')}</a>
              <div className="social">
                <a href="https://linkedin.com/in/davidorregod" target="_blank" rel="noopener noreferrer">{t('hero.linkedin')}</a>
                <a href="https://github.com/davidorregod" target="_blank" rel="noopener noreferrer">{t('hero.github')}</a>
              </div>
            </div>
            <div className="language-selector"><LanguageSelector /></div>
          </div>
        </nav>
      </header>

      {/* Hero */}
      <section className="hero">
        <div className="hero-content">
          <div className="hero-image" onClick={() => setShowImageModal(true)} title="Click para ampliar">
            <img src={profileImg} alt="Profile" className="profile-img" />
          </div>
          <div className="hero-text">
            <h1 dangerouslySetInnerHTML={renderHTML(t('hero.title'))} />
            <p className="tagline">{t('hero.tagline')}</p>
            <p className="description" dangerouslySetInnerHTML={renderHTML(t('hero.description'))} />
            <div className="hero-buttons">
              <a href="https://linkedin.com/in/davidorregod" className="btn">{t('hero.linkedin')}</a>
              <a href="https://youtube.com" className="btn">{t('hero.youtube')}</a>
              <button onClick={handleCopyEmail} className="btn email-btn">{t('hero.email')}</button>
            </div>
          </div>
        </div>
      </section>

      {/* About */}
      <section id="about" className="about-section">
        <div className="about-container">
          <div className="about-header">
            <h2>¿Quién Soy?</h2>
            <p className="about-subtitle">Aprendiz junior</p>
          </div>
          <div className="about-content">
            <div className="about-text">
              <p className="about-description">Mi nombre es David Cigarán Flores, tengo 23 años y recientemente he finalizado mis estudios de Administración de Sistemas Informáticos en Red (ASIR) y el bootcamp de Ciberseguridad en The Bridge, donde descubrí mi gusto por la seguridad ofensiva y el análisis de vulnerabilidades, más específicamente en el ámbito del Red Team.</p>
              <p className="about-description">Durante mi formación he desarrollado una base sólida en redes, sistemas y metodologías de pentesting, y sigo ampliando mis conocimientos por interés propio. Me considero una persona responsable, con muchas ganas de aprender y con la capacidad de adaptarme rápidamente a nuevos entornos. Disfruto trabajando en equipo, compartiendo ideas y enfrentando retos técnicos que me permitan crecer.</p>
              <p className="about-description">Busco una oportunidad para iniciar mi carrera en un entorno donde pueda aprender de expertos, enfrentar desafíos reales y aportar desde el primer día. Mi objetivo es seguir mejorando poco a poco, reforzar mis habilidades y llegar a ser un buen profesional en el mundo de la ciberseguridad ofensiva.</p>
            </div>
          </div>
        </div>
      </section>

      {/* Herramientas */}
      <section id="herramientas" className="section herramientas-section">
        <div className="container">
          <h2 className="section-title">Herramientas y Tecnologías</h2>
          <p className="section-subtitle">Todas las herramientas que domino en el ámbito de la ciberseguridad</p>
          <div className="herramientas-grid">
            {todasLasHerramientas.map(h => (
              <div key={h.id} className={`herramienta-card ${hoveredCard === h.id ? 'hovered' : ''}`} onMouseEnter={() => handleMouseEnter(h.id)} onMouseLeave={handleMouseLeave}>
                <div className="herramienta-content">
                  <div className="herramienta-icon-wrapper">
                    <img src={h.icon} alt={h.name} className="herramienta-icon" />
                    <div className="icon-glow"></div>
                  </div>
                  <h3 className="herramienta-name">{h.name}</h3>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Certificaciones */}
      <section id="certifications" className="section certifications-section">
        <div className="container">
          <h2 className="section-title">Certificaciones e Insignias</h2>
          <div className="certifications-carousel" ref={carouselRef}>
            <div className="carousel-wrapper">
              <div className="carousel-track" style={{ transform: `translateX(-${currentCertIndex * 100}%)` }}>
                {[...certifications, ...certifications, ...certifications].map((cert, index) => (
                  <div key={`${cert.id}-${index}`} className="carousel-slide">
                    <div className="cert-card">
                      <img src={cert.image} alt={cert.title} className="cert-image" />
                      <div className="cert-content">
                        <h3 className="cert-title">{cert.title}</h3>
                        <p className="cert-expedition">Expedición: {cert.expedition}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <button className="carousel-btn prev-btn" onClick={prevCertSlide}>‹</button>
            <button className="carousel-btn next-btn" onClick={nextCertSlide}>›</button>
            <div className="carousel-indicators">
              {certifications.map((_, index) => (
                <button key={index} className={`indicator ${index === currentCertIndex % certifications.length ? 'active' : ''}`} onClick={() => setCurrentCertIndex(index)} />
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Educación */}
      <section id="a_history" className="section education-section">
        <div className="container">
          <h2 className="section-title education-title">Historial Académico</h2>
          <div className="timeline">
            {educationHistory.map(item => (
              <div key={item.id} className="timeline-item">
                <div className="timeline-content">
                  <h3 className="timeline-title">{item.title}</h3>
                  <div className="timeline-date">{item.date}</div>
                  <p className="timeline-institution">{item.institution}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Modal */}
      {showImageModal && (
        <div className="modal-overlay" onClick={() => setShowImageModal(false)}>
          <img src={profileImg} alt="Profile ampliada" className="modal-image" onClick={e => e.stopPropagation()} />
          <button className="modal-close-btn" onClick={() => setShowImageModal(false)} aria-label="Cerrar imagen">✕</button>
        </div>
      )}

      {/* Toast */}
      {showCopied && <div className="copy-toast">📋 Email copiado al portapapeles</div>}

      {/* Footer */}
      <footer className="footer">
        <p>{t('footer.inspired')}</p>
        <p>{t('footer.copyright').replace('[AÑO]', currentYear).replace('[TU NOMBRE]', 'David Cigarán')}</p>
      </footer>

      <div className="background-effect"></div>
    </div>
  );
}

export default App;
